                if($scope.posts.length == 0){
                    $scope.noContent = true;
                }
                $scope.imageSrc = [];
                $scope.postFiles = [];
                $scope.postText = undefined;
                $scope.isImage = false;
                $scope.imgUpload = false;
                $scope.isUpload = false;
                $scope.uploadBox = false;
                $scope.postContentStart = false;
                $scope.postContentLoader = false;
                $scope.tempPostId = '';
                $('#text_lp1').val('');
                $('#text_lp1').attr("style", "");
