	var currentTimeout = null;
	var DELAY_TIME_BEFORE_POSTING = 300;

	$('#categoryKeyword').keypress(function(event) {
		if(currentTimeout) {
			$timeout.cancel(currentTimeout);
		}
		currentTimeout = $timeout(function(){
			if(event.which != 13){ 
				$scope.searchKeyword();
			}
		}, DELAY_TIME_BEFORE_POSTING)
	});

